function OnUpdate(doc, meta) {

    var id = meta.id;

    if(doc.op !== 'd'){
      var payload=doc.after;
      var time = doc.ts_ms;
      var source= doc.source;
        tgt[id]=updateMeta(payload, time,source);
        delete src[meta.id];
    }else{
       var idsrc=doc.before.id;
       delete tgt[idsrc];
       delete src[meta.id];
        log('OnUpdate: removed Customer:', idsrc);
    }

}
function updateMeta(payload,time,source) {
    let metadoc = payload.metadoc;


    if (metadoc == null) {
        metadoc =  {"table": source.table,  "db": source.db, "snapshot":source.snapshot, "connector":source.connector, "time": time};
    }
        payload.metadoc = metadoc;
    return payload;
}
